  if (request.url.query.get('accept')) {
    request.header.set('Accept', 'this is a bad header');
  }

  if (request.url.query.get('behalf')) {
    request.header.set('on-behalf-of', 'subuser2');
  }

  // For example, adding ?mock=200 to a request url will enable mocking,
  // using the example endpoint response for the 200 status code.
  // var mock = request.url.query.get("mock")
