  //  ctx.mock.set(true, mock)
  //}

  // For example, adding the header X-Mock: 200 will enable mocking,
  // using the example endpoint response for the 200 status code.
  var mock = request.header.get("X-Mock");
  if (mock) {
	  ctx.mock.set(true, mock);
	  ctx.learn.set(false);
  }
}

